CREATE TABLE news (
news_id     int    NOT NULL    AUTO_INCREMENT,
category_name  varchar(255)   NOT NULL,
news_title    text   NOT NULL,
news_content    text     NOT NULL,
img    varchar(255)  NOT NULL,
email    varchar(255)    NOT NULL,
PRIMARY KEY (news_id),
FOREIGN KEY (email) REFERENCES users(email)
);
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'খেলা', 'টস জিতে ব্যাটিংয়ে বাংলাদেশ', 'চট্টগ্রামে জহুর আহমেদ চৌধুরী স্টেডিয়ামে সিরিজের প্রথম টেস্টে পাকিস্তানের মুখোমুখি বাংলাদেশ।পাকিস্তানের বিপক্ষে টেস্টে এ পর্যন্ত ১১ ম্যাচ খেলে একটিও জিততে পারেনি বাংলাদেশ। ১০ হার ও ১ ড্র।ঘরের মাঠে পাকিস্তানের বিপক্ষে ৬ টেস্টের ৫টিতে হার ও ১ ম্যাচ ড্র করেছে বাংলাদেশ।এই সফরে টি–টোয়েন্টি সিরিজে পাকিস্তানের কাছে তিন ম্যাচ সিরিজে ৩–০ ব্যবধানে হোয়াইটওয়াশ হয়েছে মাহমুদউল্লাহর দল।', 'mmt.jpg', 'touhid@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'খেলা', 'ইংল্যান্ডকে হারিয়ে শুরুর আশা রকিবুলের', 'সেন্ট কিটসের ওয়ার্নার পার্ক মাঠটা বাংলাদেশ অনূর্ধ্ব-১৯ দলের মোটামুটি চেনাই হয়ে গেছে। অনূর্ধ্ব-১৯ বিশ্বকাপ খেলতে ওয়েস্ট ইন্ডিজে গিয়ে দুটি প্রস্তুতি ম্যাচই রকিবুল হাসানরা খেলেছেন এ মাঠে। বাংলাদেশের যুবাদের বিশ্বকাপ–পূর্ব প্রস্তুতিটাও হয়েছে এ মাঠ ঘিরেই। বাংলাদেশ সময় আজ সন্ধ্যা সাতটায় সেখানেই ইংল্যান্ডের বিপক্ষে ম্যাচ দিয়ে এবারের অনূর্ধ্ব-১৯ বিশ্বকাপে বর্তমান চ্যাম্পিয়ন বাংলাদেশের যুবারা শুরু করবেন শিরোপা ধরে রাখার যাত্রা।গতবারের শিরোপাজয়ী হিসেবে এবারের বিশ্বকাপে সবারই বাড়তি কৌতূহল রকিবুলদের নিয়ে। ক্যারিবিয়ান দ্বীপপুঞ্জে পৌঁছানোর পর থেকেই ‘ফেবারিট’ তকমার সঙ্গে মানিয়ে নিতে হচ্ছে তাদের। তবে বাংলাদেশের অধিনায়ক এখনই দূরের লক্ষ্য নিয়ে ভাবছেন না। ম্যাচ ধরে ধরে খেলে গ্রুপপর্বের বাধা উতরানোটাই প্রাথমিক লক্ষ্য তাঁর।ইংল্যান্ডের বিপক্ষে ম্যাচের আগে গতকাল রকিবুল বলছিলেন, ‘আমরা ম্যাচ ধরে ধরে এগোতে চাই। নিজেদের যে পরিকল্পনা আছে, সেগুলো বাস্তবায়ন করতে চাই। আগামীকাল (আজ) ইংল্যান্ডের বিপক্ষে ম্যাচ, এরপর আরও দুটি ম্যাচ আছে। সেগুলোতে ভালো করে পরের ধাপে যেতে চাই।’বাংলাদেশ আর ইংল্যান্ড ছাড়া গ্রুপ ‘এ’–এর বাকি দুই দল সংযুক্ত আরব আমিরাত ও কানাডা। কাগজে-কলমে বাংলাদেশ আর ইংল্যান্ডকে ‘এ’ গ্রুপের দুই ফেবারিটই বলা যায়। আজ দুই ফেবারিটের লড়াই জিততে ওয়ার্নার পার্কের কন্ডিশনের সঙ্গেও লড়তে হবে রকিবুলদের। রকিবুল গতকাল মুঠোফোনে বলছিলেন-যে মাঠে খেলা হবে, সেখানে আমরা দুটি প্রস্তুতি ম্যাচ খেলেছি। উইকেট বেশ ভালো, পেসাররা বেশ সাহায্য পাবে। ব্যাটিংয়ের জন্যও ভালো এই উইকেট। স্পিনাররা উইকেট থেকে খুব বেশি সাহায্য পাবে না। এখন দেখা যাক আমরা কতটা মানিয়ে নিতে পারি। আশা করি জয় দিয়ে শুরু করতে পারব।', 'u19.jpg', 'touhid@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'খেলা', 'টি–টোয়েন্টিতে এখন থেকে ধীর বোলিংয়ে ম্যাচেই শাস্তি', 'আন্তর্জাতিক ম্যাচে এত দিন স্লো ওভার রেটের কারণে ম্যাচের পর জরিমানা ও নিষেধাজ্ঞার মতো শাস্তি দিয়েছে আইসিসি। নিয়মটি পাল্টে এখন থেকে টি-টোয়েন্টিতে নতুন নিয়ম চালু করেছে ক্রিকেটের নিয়ন্ত্রক এই সংস্থা। টি-টোয়েন্টিতে স্লো ওভার রেটের কারণে এখন থেকে ম্যাচ চলার সময়ই শাস্তি পেতে হবে। আজ সংবাদ বিজ্ঞপ্তিতে জানিয়েছে আইসিসি। ছেলে-মেয়েদের আন্তর্জাতিক টি-টোয়েন্টি ক্রিকেটে চালু করা হয়েছে নিয়মটি। এ ছাড়া বাড়তি একটি পানি পানের বিরতিও রাখা হয়েছে নতুন প্লেয়িং কন্ডিশনে।আইসিসির প্লেয়িং কন্ডিশনে ১৩.৮ ধারায় স্লো ওভার রেটে শাস্তির নতুন নিয়মটি চালু করা হয়েছে। বলা হয়েছে, নির্ধারিত কিংবা পূর্বনির্ধারিত সময় অনুযায়ী ফিল্ডিং দল ইনিংসের শেষ ওভারের প্রথম বল করার মতো অবস্থানে থাকবে। ফিল্ডিং দল যদি এ অবস্থায় না থাকে তাহলে মাঠের আম্পায়ারের হস্তক্ষেপে তৎক্ষণাৎ শাস্তি পেতে হবে—ইনিংসের বাকি সময়ে ফিল্ডিং দল ৩০ গজের বাইরে একজন ফিল্ডার কম নিয়ে ফিল্ডিং করবে। আইসিসি ক্রিকেট কমিটি সুপারিশের ভিত্তিতে নিয়মটি চালু করেছে। ইসিবি আয়োজিত ‘হানড্রেড’ টুর্নামেন্টে নিয়মটি চালু করে সফল হওয়ার পর এবার আইসিসিও তা লুফে নিল।৩০ গজের বাইরে একজন ফিল্ডার কম থাকলে ব্যাটসম্যানদের যে সুবিধা হবে, তা নিয়ে কোনো সন্দেহ নেই। এদিকে ইনিংসে বেঁধে দেওয়া সময়ের মধ্যে ওভারের কোটা শেষ করার ক্ষেত্রে সাম্প্রতিক সময়ে স্লো ওভার রেটের নজির খুব কম নেই। মাঝেমধ্যেই দলগুলো এ জন্য শাস্তি পাচ্ছে। ১৬ জানুয়ারি শুরু হতে যাওয়া ছেলেদের ওয়েস্ট ইন্ডিজ-আয়ারল্যান্ড সিরিজ থেকে এ নিয়ম চালু হবে। ১৮ জানুয়ারি দক্ষিণ আফ্রিকা-ওয়েস্ট ইন্ডিজ সিরিজ দিয়ে মেয়েদের ক্রিকেটে নিয়মটি চালু হবে।বাড়তি পানি পানের বিরতি প্রসঙ্গে বলা হয়, প্রতি ইনিংসের মাঝপথে রাখা হতে পারে এই বিরতি। তবে সিরিজ শুরুর আগে দুই দলকে এ ব্যাপারে সম্মতি দিতে হবে। আড়াই মিনিট করে এই বিরতি চলবে। গত টি-টোয়েন্টি বিশ্বকাপে এ নিয়ম চালু করলেও এখন থেকে প্রতিটি আন্তর্জাতিক টি-টোয়েন্টি সিরিজেই তা ব্যবহার করতে চায় আইসিসি।', 'icc.jpg', 'touhid@gmail.com');

INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'ধর্ম', 'দাম্পত্যজীবন আল্লাহর অশেষ নিয়ামত', 'স্বামী-স্ত্রী সম্পর্কটি মানবসভ্যতার ইতিহাসের প্রথম ও সবচেয়ে ঘনিষ্ঠতম এবং শক্তিশালী সম্পর্ক। স্বামী স্ত্রীকে থাকার জন্য যে ঘর বা কক্ষ দেবেন, সে ঘর বা কক্ষে স্ত্রীর অনুমতি ছাড়া (স্বামী ব্যতীত) কেউই প্রবেশ করতে পারবেন না। এমনকি স্বামীর মা, বাবা, ভাইবোনও নন। স্ত্রীর ব্যক্তিগত নিরাপত্তা ও গোপনীয়তা রক্ষার স্বার্থে প্রয়োজনে এই ঘরে বা কক্ষে তিনি তালা–চাবিও ব্যবহার করতে পারেন। স্ত্রীর ব্যক্তিগত বা গোপনীয় বিষয়ে স্বামী ছাড়া কেউ নাক গলাতে পারবেন না। স্ত্রীর স্যুটকেস, ট্রাংক ও আলমারি স্বামী ছাড়া কেউ তল্লাশি করতে পারবেন না। কোনো স্ত্রীর চলাফেরা বা আচার-আচরণ শ্বশুর-শাশুড়ির অপছন্দ হলে তাঁকে আলাদা বাড়ি বা ঘর করে দিতে হবে। (শরহে বেকায়া)। নবী করিম সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম হজরত উমর (রা.)-কে তাঁর ছেলে আবদুল্লাহ (রা.)-এর স্ত্রীর ব্যাপারে এরূপ নির্দেশনা দিয়েছিলেন। স্বামীর সংসারে স্ত্রী কাজকর্ম করলে নফল ইবাদতের সওয়াব পাবেন। অসচ্ছল ও অভাবী স্বামীর সংসারে স্ত্রী কাজকর্ম করলে স্বামীর উপার্জনে সহায়তা এবং সন্তানদের প্রতি সহযোগিতা করা হবে। সংসারে উন্নতির জন্য স্বামী-স্ত্রী একে অন্যকে সহযোগিতা করা উচিত। পরামর্শের ভিত্তিতে কাজ করলে সংসারে সুখ-শান্তি ও সমৃদ্ধি আসবে, ইনশা আল্লাহু তাআলা।', 'biya.jpg','tou@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'ধর্ম', 'ইসলামে সময়কে কেন এত গুরুত্ব দেওয়া হয়েছে', 'সময় আল্লাহর দান। সময়ের সমষ্টিই জীবন। মানবজীবনে সময়ের গুরুত্ব সবচেয়ে বেশি, তাই সময়ের মূল্যায়ন করা অপরিহার্য। আল্লাহ তাআলা বলেন, ‘মহাকালের শপথ! মানুষ অবশ্যই ক্ষতিগ্রস্ত, কিন্তু তারা নয়; যারা বিশ্বাস করে ও সৎকর্ম করে এবং পরস্পরকে সত্যের উপদেশ দেয় ও ধৈর্য ধারণে সহায়তা করে।’ (সুরা আসর, আয়াত: ১-৩)।সময়ের হিসাবের নিমিত্তে আল্লাহ তাআলা চন্দ্র ও সূর্য সৃষ্টি করেছেন। কোরআনের বর্ণনা, ‘তিনিই সূর্যকে তেজস্কর ও চন্দ্রকে জ্যোতির্ময় করেছেন এবং তাদের মনজিল নির্দিষ্ট করেছেন, যাতে তোমরা বছর গণনা ও সময়ের হিসাব জানতে পারো। আল্লাহ এসব নিরর্থক সৃষ্টি করেননি। জ্ঞানী সম্প্রদায়ের জন্য তিনি এসব নিদর্শন বিশদভাবে বিবৃত করেন।’ (সুরা: ১০ ইউনুস, আয়াত: ৫)। ‘আর সূর্য ভ্রমণ করে তার নির্দিষ্ট গন্তব্যের দিকে, ইহা পরাক্রমশালী সর্বজ্ঞের নিয়ন্ত্রণ। এবং চন্দ্রের জন্য আমি নির্দিষ্ট করেছি বিভিন্ন মনজিল; অবশেষে সেটি শুষ্ক বক্র পুরোনো খর্জুর শাখার আকার ধারণ করে।’ (সুরা: ৩৬ ইয়াসিন, আয়াত: ৩৮-৩৯)। ‘নিশ্চয়ই আকাশমণ্ডলী-পৃথিবী সৃষ্টির দিন থেকেই আল্লাহর বিধানে আল্লাহর নিকট মাস গণনায় মাস ১২টি।’ (সুরা: ৯ তাওবা, আয়াত: ৩৬)।', 'time.jpg','tou@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'ধর্ম', '৫ মাসেই হাফেজ ৯ বছরের শিশু', 'মাত্র পাঁচ মাসে পবিত্র কোরআনের হাফেজ হয়েছে নয় বছরের এক শিশু।শিশুটির নাম সিয়াম। তার বাড়ি কুমিল্লার চান্দিনা উপজেলার লোনা গ্রামে। বাবার নাম হায়াতুল্লাহ।কুমিল্লা শহরের মোগরটুলি এলাকার আন-নূর তাহফিজ মাদ্রাসার হিফজ বিভাগের শিক্ষার্থী সিয়াম। এই বিভাগ থেকেই সে পবিত্র কোরআনের হাফেজ হয়েছে।হিফজ বিভাগের শিক্ষক হাফেজ মাওলানা মাহদী হাসান বাসসকে বলেন, ‘সিয়াম দেশের বিস্ময়বালক। তার মেধা সাধারণের চেয়ে অনেক বেশি। সিয়াম মাত্র পাঁচ মাসে পবিত্র কোরআনের ৩০ পারাই মুখস্থ করেছে।মাহদী হাসান আরও বলেন, শিশু সিয়াম চঞ্চল প্রকৃতির। তার পক্ষে আরও শিগগির হাফেজ হওয়া সম্ভব ছিল।ওই শিক্ষক বলেন, সিয়ামের ঐকান্তিক ইচ্ছা, শিক্ষকদের সম্মিলিত প্রচেষ্টা ও পরিবারের সহযোগিতায় সে এত অল্প বয়সেই এই কীর্তি গড়েছে।', 'hafez.jpg','tou@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'ধর্ম', 'পবিত্র ওমরাহ পালনে নতুন নির্দেশনা সৌদি আরবের', 'পবিত্র ওমরাহ পালনের জন্য নতুন নির্দেশনা দিয়েছে সৌদি আরব। নতুন নির্দেশনা অনুযায়ী, প্রথমবার পবিত্র ওমরাহ পালনের অন্তত ১০ দিন পর দ্বিতীয়বার ওমরাহ পালন করা যাবে। এ নির্দেশনা সব বয়সী মানুষের জন্য। গতকাল বুধবার দেশটির সৌদি গেজেটের এক প্রতিবেদনে এসব কথা বলা হয়েছে। সৌদি আরবের হজ ও ওমরাহ মন্ত্রণালয়ের টুইটারেও এ নতুন নির্দেশনা জারি করা হয়েছে।টুইটের নির্দেশনায় বলা হয়েছে, প্রথমবার ওমরাহ পালন শেষে দ্বিতীয়বার ওমরাহ পালনে ইচ্ছুক ব্যক্তিদের ইটামারনা বা তাওয়াক্কলনা অ্যাপের মাধ্যমে আবার আবেদন করতে হবে। ১০ দিন পর তাঁদের ওমরাহ পালনে অনুমতি দেওয়া হবে।সৌদি আরবে গত মাসে করোনার সংক্রমণ একলাফে অনেক বেড়ে গেছে। করোনার নতুন ধরন ওমিক্রনের বিস্তার ঠেকাতে আবার সামাজিক দূরত্বের বিধিনিষেধ আরোপ করেছে। সৌদি আরব সব জায়গায় মাস্ক পরা এবং সামাজিক দূরত্ব বজায় রেখে চলার জন্য বাধ্যবাধকতা জারি করেছে। এর সঙ্গে মিলিয়ে মক্কা ও মদিনার পবিত্র দুই মসজিদে একই বিধান চালু করা হয়েছে।এর আগে গত বছর দুই উমরাহ পালনের মধ্যে ১৫ দিনের বিরতির কথা বলা হয়েছে। গত বছরের অক্টোবরে সৌদি আরব এ নির্দেশনা প্রত্যাহার করে নেয়। আবার করোনার সংক্রমণ বেড়ে যাওয়ায় ১০ দিন বিরতির নতুন নির্দেশনা দেওয়া হলো।', 'Saudi.jpg','touhid@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'ধর্ম', 'ইসলামে প্রশংসা ও সমালোচনা করার মূলনীতি', 'সব প্রশংসা আল্লাহ তাআলার জন্য। তিনি অতি পবিত্র, সব ধরনের দোষত্রুটিমুক্ত। মানুষের মধ্যে ভালো ও মন্দের উভয় সম্ভাবনা বিদ্যমান। আল্লাহ তাআলা বলেন, ‘শপথ মানুষের এবং তাঁর, যিনি তাকে প্রতিষ্ঠিত করেছেন। অতঃপর তাকে সৎ কর্ম ও অসৎ কর্মের জ্ঞান দান করেছেন। সে-ই সফল হবে, যে নিজেকে পবিত্র করবে। আর সে-ই ব্যর্থ হবে, যে নিজেকে কলুষিত করবে।’ (সুরা-৯১ শামস, আয়াত: ৭-১০)।গুণের প্রশংসা ও দোষের সমালোচনা স্বাভাবিক একটি বিষয়। ইসলাম মানুষের প্রশংসা বা সমালোচনা উভয় ক্ষেত্রে ভারসাম্য রক্ষার নির্দেশনা দিয়েছে। যেকোনো সময়, স্থান, বস্তু বা ব্যক্তির সঠিক প্রশংসা করা হলে তা বাস্তবে আল্লাহ তাআলারই প্রশংসা হয়। কারণ, এসবেরই স্রষ্টা তিনি। (তাফসিরে মাআরিফুল কোরআন)।গুণীর গুণের প্রশংসা করা সততা ও নৈতিকতার দাবি। আল্লাহ রাব্বুল আলামিন নিজেই অনেকের প্রশংসা করেছেন। ইয়াহইয়া (আ.)-এর ব্যাপারে বলেছেন, ‘সে হবে আল্লাহর বাণীর সমর্থক, নেতা, সংসারবিবাগি এবং অন্যতম পুণ্যবান নবী।’ (সুরা-৩ আলে ইমরান, আয়াত: ৩৯)। রাসুলুল্লাহ (সা.)-ও সাহাবিগণের প্রশংসা করেছেন। হজরত উমর (রা.) সম্পর্কে বলেছেন, ‘তুমি যে পথে চলো শয়তান কখনো সে পথে চলে না, বরং সে তোমার পথ ছেড়ে অন্য পথে চলে।’ (বুখারি, হাদিস: ৩২৯৪)। হজরত আবু হুরায়রা (রা.) থেকে বর্ণিত, রাসুলুল্লাহ (সা.) বলেছেন, ‘মানুষের মধ্যে ভালো লোক আবু বকর, ভালো লোক উমর, ভালো লোক আবু উবাইদা, ভালো লোক উসআদ ইবনে উজাইর, ভালো লোক সাবিত ইবনু কায়স, ভালো লোক আমর ইবনুল জামুহ ও ভালো লোক মুআজ ইবনু জাবাল!’ (আদাবুল মুফরাদ)।প্রশংসায় কৃতজ্ঞতা প্রকাশ করা হয়। রাসুলুল্লাহ (সা.) বলেন, ‘যারা মানুষের কৃতজ্ঞতা আদায় করে না, সে আল্লাহর কৃতজ্ঞতা আদায় করতে পারে না।’ (মুসনাদে আহমাদ, হাদিস: ২১৩৩১)। প্রশংসায় ভালো কাজে উৎসাহিত করা হয়। নবীজি (সা.) বলেছিলেন, ‘আবদুল্লাহ কতই না উত্তম ব্যক্তি যদি সে তাহাজ্জুদ আদায় করত।’ (বুখারি, হাদিস: ১১৫৬)। প্রশংসায় অধিকার লাভ করা যায়। হজরত শোয়াইব (আ.)-এর কন্যা মুসা (আ.) সম্পর্কে বলেছিলেন, ‘হে পিতা! আপনি একে কর্মে নিযুক্ত করুন। কেননা আপনার কর্মী হিসেবে উত্তম হবে সেই ব্যক্তি, যে শক্তিশালী, বিশ্বস্ত।’ (সুরা-২৮ কাসাস, আয়াত: ২৬)।', 'praise.jpg','touhid@gmail.com');
INSERT INTO news (category_name, news_title, news_content, img, email) VALUES ( 'বিজ্ঞান', 'পৃথিবীর বায়ুমণ্ডল কত বড়?', 'ব্যারোমিটারের উদ্ভাবক বিজ্ঞানী টরিসেলি বলেছিলেন, ‘বিশাল এক বায়ুসমুদ্রের তলদেশে আমরা ডুবে আছি।’ আসলেই তাই। সেই সমুদ্রের তলদেশে আমরা মানুষ বাস করছি পিঁপড়ার মতো। আর আমাদের নিয়ে মহাকাশে ভেসে বেড়াচ্ছে আমাদের নীল গোল গ্রহটি। পৃথিবীর চারপাশ ঘিরে আছে বায়ুমণ্ডল। বায়ুমণ্ডলের চার ভাগের তিন ভাগই আছে ভূপৃষ্ঠ থেকে মাত্র ১১ কিলোমিটারের মধ্যে। আরও দূরে যেতে থাকলে ক্রমেই ছোট হয়ে আসে বায়ুর পুরুত্ব। কিন্তু পৃথিবীর বায়ুমণ্ডলের শেষ বিন্দুটি ঠিক কোথায় অবস্থিত? এ বিষয়ে নেই কোনো সুস্পষ্ট সংজ্ঞা।পৃথিবীর পৃষ্ঠ থেকে ১০০ কিলোমিটার দূরের একটি রেখার নাম কারমান রেখা। একে অনেক সময় বহিস্থ মহাকাশ আর পৃথিবীর মধ্যের সীমানা ধরা হয়। রেখাটির দূরত্ব পৃথিবীর ব্যাসার্ধের ১.৫৭ অংশ। তবে মহাকাশযান পৃথিবীতে ফিরে আসার সময় কিন্তু ১২০ কিলোমিটার দূরে থাকতেই বায়ুমণ্ডলের কণার সঙ্গে ধাক্কা খেতে শুরু করে। এ ধাক্কায় যেন ক্ষতি না হয় সে জন্যও মহাকাশযানে বিশেষ ধরনের প্রতিরক্ষাব্যবস্থা থাকে।তাহলে পৃথিবীর বায়ুমণ্ডল আসলে কোন পর্যন্ত? এ বিষয়ে সম্প্রতি একটি নির্ভরযোগ্য উত্তর দিয়েছে ইউরোপীয় স্পেস এজেন্সি এসা (ESA)। এ কাজে তারা ব্যবহার করেছে সোহো অবজারভেটরির ২০ বছরের পুরোনো উপাত্ত। তাদের প্রকাশিত ফলাফল যথেষ্ট চমকপ্রদ। পৃথিবীর বায়ুমণ্ডলের একেবারে বাইরের অঞ্চলের নাম জিওকরোনা। আর এই জিওকরোনার বিস্তৃতি চাঁদের সীমানা থেকেও বহুদূরে। জিও কথাটার মানে হলো পৃথিবী। আর করোনা মানে মুকুট। তার মানে জিওকরোনার অর্থ দাঁড়ায় পৃথিবীর মুকুট।', 'atmosphere.jpg','touhid@gmail.com');



CREATE TABLE comment (
comment_id    int    NOT NULL   AUTO_INCREMENT,
comment_content     text    NOT NULL,
news_id    int     NOT NULL,
PRIMARY KEY(comment_id),
FOREIGN KEY (news_id) REFERENCES news(news_id)
);

